///<jscompress sourcefile="PxerHtmlParser.class.js" />
'use strict';

class PxerHtmlParser{
    constructor(config){
        this.config =config;
        this.task =null;
    };
};


PxerHtmlParser.prototype['parseAll'] =function(requests){
    var result =[];
    for(let request of requests){
        result.push(...this.parse(request));
    };

    return result;
};
PxerHtmlParser.prototype['parse'] =function(request){
    if(request instanceof PxerWorksRequest){
        this.task =request;
        return [this.parseWorks()];
    }else if(request instanceof PxerPageRequest){
        this.task =request;
        return this.parsePage();
    }else{
        return false;
    };
};


PxerHtmlParser.prototype['parseWorks'] =function(){
    var pw;
    for(let url in this.task.html){
        let data ={
            task :this.task,
            dom :(new DOMParser()).parseFromString(this.task.html[url] ,'text/html'),
            url  :url,
            pw   :pw,
        };
        switch (true){
            case /mode=medium/.test(url):
                pw =PxerHtmlParser.parseMediumHtml(data);
                break;
            case /mode=big/.test(url):
            case /mode=manga_big/.test(url):
                pw =PxerHtmlParser.parseMangaBigHtml(data);
                break;
            case /mode=manga&/.test(url):
                pw =PxerHtmlParser.parseMangaHtml(data);
                break;
            default:
                console.warn(`Count not parse "${url}"`);
        };
    };

    return pw;

};
PxerHtmlParser.prototype['parsePage'] =function(){


    var dom =(new DOMParser()).parseFromString(this.task.html ,'text/html');
    var elts =dom.querySelectorAll('a.work._work');


    var taskList =[];
    for(let elt of elts){
        let task =new PxerWorksRequest({
            html        :{},
            type        :elt.matches('.ugoku-illust')?'ugoira'
                :elt.matches(".manga")?'manga'
                :"illust"
            ,
            isMultiple  :elt.matches(".multiple"),
            id          :elt.getAttribute('href').match(/illust_id=(\d+)/)[1]
        });

        task.url =getUrlList(task);

        taskList.push(task);
    };

    return taskList;

    function getUrlList(task ,singleMangaMode){
        if(
            task.type ==="ugoira"
            ||(
                task.type ==="illust"
                && !task.isMultiple
            )
        ){
            return ["/member_illust.php?mode=medium&illust_id="+task.id];
        }else if(task.isMultiple){
            return [
                "/member_illust.php?mode=medium&illust_id="+task.id,
                "/member_illust.php?mode=manga&illust_id="+task.id,
                "/member_illust.php?mode=manga_big&page=0&illust_id="+task.id
            ];
        }else if(task.type ==="manga" && !task.isMultiple){
            return [
                "http://www.pixiv.net/member_illust.php?mode=medium&illust_id="+task.id,
                "http://www.pixiv.net/member_illust.php?mode=big&illust_id="+task.id,
            ];
        }else{
            console.warn('miss task '+task.id);
            return [];
        };
    };


};


PxerHtmlParser.parseMangaHtml =function({task,dom,url,pw}){
    var rePw =new PxerMultipleWorks(pw);
    rePw.multiple =+dom.querySelectorList(".page .total",".position .total").innerHTML;
    return rePw;
};
PxerHtmlParser.parseMangaBigHtml =function({task,dom,url,pw}){
    var rePw =new PxerMultipleWorks(pw);
    var src =dom.getElementsByTagName('img')[0].src;
    Object.assign(rePw ,{
        server :src.match(/(i\d+)\.pixiv\.net/)[1],
        date   :src.match(PxerHtmlParser.REGEXP['getDate'])[1],
        fileFormat:src.match(/\.(jpg|gif|png)$/)[1],
    });
    return rePw;
};
PxerHtmlParser.parseMediumHtml =function({task,dom,url,pw}){

    var rePw =new PxerWorks({
        id :task.id,
        type :task.type,
        tagList :[...dom.querySelectorAll(".tag a.text")].map(elt=>elt.innerHTML),
        viewCount :+dom.querySelector(".view-count").innerHTML,
        ratedCount :+dom.querySelector(".rated-count").innerHTML,
        scoreCount :+dom.querySelector(".score-count").innerHTML,
        fileFormat :'jpg',
    });

    if(task.type ==='ugoira'){
        let script =[...dom.querySelectorAll("script")]
                .filter(tag=>/zip/.test(tag.innerHTML))[0]
                .innerHTML
            ;
        let exp =/"src":"([^"<>]*?600x600\.zip)"[^<>]*?"frames":(\[.*?\])/mi;
        let arr =script.match(exp);

        rePw =new PxerUgoiraWorks(Object.assign(rePw ,{
            server :arr[1].replace(/\\\//g ,'\/').match(/(i\d+)\.pixiv\.net/)[1],
            date   :arr[1].replace(/\\\//g ,'\/').match(PxerHtmlParser.REGEXP['getDate'])[1],
            frames :JSON.parse(arr[2]),
        }));

    };

    if(task.type ==='illust' &&!task.isMultiple){
        let src =dom.querySelector(".ui-modal-close-box img.original-image").getAttribute("data-src");
        Object.assign(rePw ,{
            server :src.match(/(i\d+)\.pixiv\.net/)[1],
            date   :src.match(PxerHtmlParser.REGEXP['getDate'])[1],
            fileFormat:src.match(/\.(jpg|gif|png)$/)[1],
        });
    }

    if(task.type ==='manga' &&!task.isMultiple){
        let src =dom.querySelector("a._work.manga img").src;
        Object.assign(rePw ,{
            server :src.match(/(i\d+)\.pixiv\.net/)[1],
            date   :src.match(PxerHtmlParser.REGEXP['getDate'])[1],
        });
    }




    return rePw;

};
PxerHtmlParser.getMangaPath =function(pwr){

    var pw =PxerHtmlParser.parseMediumHtml({
        task :pwr,
        dom  :(new DOMParser()).parseFromString(pwr.html[pwr.url[0]] ,'text/html'),
        url  :pwr.url[0],
        pw   :{},
    });

    pw.fileFormat ='';
    return PxerPrint.getWorks(pw,{manga_single:'max'})[0];

};


PxerHtmlParser.REGEXP ={
    'getDate':/img\/((?:\d+\/){5}\d+)/,
};










