///<jscompress sourcefile="PxerThread.class.js" />
'use strict';

class PxerThread extends PxerEvent{
    constructor({id ,config ,task}){
        super(['load','error','fail']);
        /*!当前线程的ID*/
        this.id =id;
        /*!当前线程是否空闲*/
        this.isFree=true;
        /*!线程执行的任务*/
        this.task =task;

        this.config =config ||{
            /*!ajax超时重试时间*/
            timeout:5000,
            /*!最多重试次数*/
            retry:3
        };

        /*!运行时参数*/
        this.runtime ={};

        /*!使用的xhr对象*/
        this.xhr =new XMLHttpRequest();
        this.xhr.timeout =this.config.timeout;
        this.xhr.responseType ='text';

    };
};


PxerThread.checkRequest =function(url ,html){
    if(!html) return 'empty';
    if(html.indexOf("_no-item _error") !==-1){
        if(html.indexOf("sprites-r-18g-badge") !==-1) return 'r-18g';
        if(html.indexOf("sprites-r-18-badge") !==-1) return 'r-18';
    };
    if(html.indexOf("sprites-mypixiv-badge") !==-1) return 'mypixiv';
    return true;
};


PxerThread.prototype['stop'] =function(){
    this.xhr.abort();
};


PxerThread.prototype['init'] =function(task){
    if(task) this.task=task;

    this.runtime ={};
    this.isFree =false;

    //判断行为，读取要请求的URL
    if(this.task instanceof PxerWorksRequest){
        this.runtime.urlList =this.task.url.slice();
    }else if(this.task instanceof PxerPageRequest){
        this.runtime.urlList =[this.task.url];
    }else{
        console.error(`PxerThread: "${this.task}" is not PxerRequest`);
        return false;
    };

};
PxerThread.prototype['run'] =function(){

    var prms =Promise.resolve();
    var thread =this;
    var xhr =this.xhr;
    xhr.removeEventListener('*');

    this.runtime.urlList.forEach((url)=>{
        prms =prms.then(()=>{return new Promise((resolve ,reject)=>{

            var sendAjax =function(){
                //绑定超时事件
                xhr.addOneEventListener("timeout" ,function(){
                    setDefalut(thread.runtime,"retry",0);
                    if( (+thread.config.retry!==0)&&(++thread.runtime.retry >=thread.config.retry) ){
                        reject({
                            type:"timeout",
                            url,
                            xhr,
                        });
                    }else{
                        sendAjax();
                    };

                });
                // 单副漫画请求需要更改Referer头信息
                if(
                    thread.task.type ==='manga'
                    && thread.task.isMultiple===false
                    && /mode=big/.test(url)
                ){
                    var referer;
                    for(let url of thread.task.url){
                        if(/mode=medium/.test(url)){
                            referer =url;
                            break;
                        }
                    };
                    xhr.open('GET' ,url ,true);
                    xhr.refererSend(referer);
                }else{
                    xhr.open('GET' ,url ,true);
                    xhr.send();
                };
            };

            xhr.addOneEventListener("load" ,function(){
                if(/^2/.test(xhr.status) ||xhr.status===304){
                    // 判断是否真的请求成功
                    var msg =PxerThread.checkRequest(url ,xhr.responseText);
                    if(msg !==true){
                        reject({
                            type:msg,
                            url,
                            xhr,
                        });
                        return;
                    };

                    // 执行成功回调
                    if(thread.task instanceof PxerWorksRequest){
                        resolve(Object.assign(
                            thread.task.html=Object(thread.task.html) ,
                            {[url]:xhr.responseText}
                        ));
                    }else{
                        resolve(thread.task.html=xhr.responseText);
                    };
                }else{
                    reject({
                        type:"http:"+xhr.status,
                        url,
                        xhr,
                    });
                };
            });
            xhr.addOneEventListener("error" ,function(){
                reject({
                    type:"error",
                    url,
                    xhr,
                });
            });

            sendAjax();

        });});
    });

    //处理回调
    prms.finally(()=>{
        thread.isFree =true;
    }).then(()=>{
        thread.dispatch("load" ,thread.task);
    }).catch(function({type,url,xhr}){
        switch(type){
            case 'error':
                console.error(`PxerThread #${thread.id} error!`);
                thread.dispatch("error" ,{task:thread.task ,msg:type});
                break;
            case 'empty':
            case 'r-18g':
            case 'r-18':
            case 'mypixiv':
                console.warn(`Request check return ${type} form PxerThread#${thread.id} @${url}`);
                break;
            case 'timeout':
                console.warn(`Request timeout form PxerThread#${thread.id} @${url}`);
                break;
            default:
                console.error(`PxerThread #${thread.id} error!`);
                thread.dispatch("error" ,{task:thread.task ,msg:'unknown'});
        };
        thread.dispatch("fail" ,{task:thread.task ,msg:type});
    });

    return true;

};


